const hsla = {
  primary: '227, 51%, 25%',
  secondary: '12, 80%, 53%',
};

export const getColor = (getColorOptions: {
  type: 'primary' | 'secondary';
  opacity: number;
}) => {
  const { type, opacity } = getColorOptions;
  const opacityValue = opacity / 100;
  const toHsla = `hsla(${hsla[type]}, ${opacityValue})`;
  return toHsla;
};
