const hsla = {
  primary: '227, 51%, 25%',
  secondary: '12, 80%, 53%',
};

export const getColor = (transparency: number) => {
  const transparencyValue = transparency / 100;
  const toHsla = `hsla(${hsla.primary}, ${transparencyValue})`;
  return toHsla;
};
