const hsla = {
  primary: '227, 51%, 25%',
  secondary: '12, 80%, 53%',
};

export const getColor = (getColorOptions: {
  type: string;
  opacity: number;
}) => {
  const { type, opacity } = getColorOptions;

  const opacityValue = opacity / 100;
  const toHsla = `hsla(${hsla.primary}, ${opacityValue})`;
  return toHsla;
};
